'use strict';

var prime = require('prime'),
	BlockBase = require('./base');

var ContentBlock = prime({
	inherits: BlockBase,

	meta: {
		type: 'content',
		name: 'Content'
	},

	formSpec: {
		pages: [{groups: ['group1']}],
		groups: {
			group1: {fields: ['title', 'description']}
		},
		fields: {
			title: {type: 'text', label: 'Title'},
			description: {type: 'textarea', label: 'Description'}
		}
	}
});

module.exports = ContentBlock;
